var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppPersistentLoginsSearch',
    Heading:'AppPersistentLogins',
    Username:{label :'Username'},
    Series:{label :'Series'},
    Token:{label :'Token'},
    LastUsed:{label :'LastUsed'},
    AppPersistentLogins:{Heading:'AppPersistentLogins',
        Username:{header:'Username'},
        Series:{header:'Series'},
        Token:{header:'Token'},
        LastUsed:{header:'LastUsed'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppPersistentLogins?'+req._parsedUrl.query;
    dao.searchAppPersistentLogins(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppPersistentLoginsSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
